CMatrixModePicture::CMatrixModePicture(uint8_t width, uint8_t height)
: CBaseMatrixMode(width, height) {
    leds = new CRGB[width * height];
    index = 0;
    indexChange = true;
    //copyPicture(0);
}

void CMatrixModePicture::draw(CRGB *leds) {

    if (millis() - tMillis > 1000) {
        //memcpy(leds, this->leds, sizeof(leds));
        tMillis = millis();
        if (indexChange) {
            indexChange = false;
            if (++index >= PICTURES) {
                index = 0;
            }
            log_d("Changed picture index to %i", index);

            uint16_t size = width * height;
            for (uint16_t i = 0; i<size; i++) {
                uint8_t r, g, b;
                uint16_t bi = i*4;

                b = bitmap[index][bi] * configuration.ledBrightness;
                g = bitmap[index][bi+1] * configuration.ledBrightness;
                r = bitmap[index][bi+2] * configuration.ledBrightness;

                leds[i] = CRGB(r, g, b);
            }
        }
    }
    
}

void CMatrixModePicture::copyPicture(uint8_t i) {
    uint16_t size = width * height;
    for (uint16_t i = 0; i<size; i++) {
        uint8_t r, g, b;
        uint16_t bi = i*4;

        b = bitmap[index][bi] * configuration.ledBrightness;
        g = bitmap[index][bi+1] * configuration.ledBrightness;
        r = bitmap[index][bi+2] * configuration.ledBrightness;

        this->leds[i] = CRGB(r, g, b);
    }
}

#ifdef KEYPAD
void CMatrixModePicture::keyEvent(key_status_t key) {
    switch (key) {
        case KEY_MIDDLE: indexChange = true; break;
        default: ;
    }
}
#endif


